css:
 body {
            font-family: Arial, sans-serif;
            direction: rtl;
            text-align: right;
            display: flex;
            flex-direction: column;
            min-height: 100vh;
            margin: 0;
        }
        main {
            flex: 1;
        }
        footer {
            text-align: center;
            padding: 10px;
            width:100%;
        }
        h1 {
            text-align: center;
            padding:20px;
        }
.field {
    display: grid;
    grid-template-columns: repeat(4, 1fr);
    gap: 25px; /* زيادة المسافة بين الأعمدة */
    margin-bottom: 15px;
}

.field label, .field span {
    margin-left: 10px; 
    text-align: right; /* لضمان أن النص يبدأ من اليمين */
}
.totals {
            margin-top: 20px;
        }
        .totals table {
            width: 100%;
            border-collapse: collapse;
        }
        .totals th, .totals td {
            border: 1px solid #ccc;
            padding: 10px;
            text-align: center;
        }
